import './App.css';

import { useState } from 'react';

function App() {

  const [value, setValue] = useState('');

  const handleKeyDown = (e) => {
    if (e.key === "Backspace") {
      setValue("")
    }
    else if (!isNaN(e.key)) {
      setValue(e.key)
    }
  }

  return (
    <div className="App">
      <div className='timer'>
        <input type="text" placeholder='_' value={value} onKeyDown={handleKeyDown} onChange={() => { }} /> minutes
      </div>
      <div className='footer'> Time saved: 100 hours &nbsp; &nbsp;</div>
    </div>
  );
}

export default App;
