import './App.css';

import { useState } from 'react';

function App() {

  const [value, setValue] = useState('');

  const handleValidateKey = (e) => {
    console.log(e.key)
    if (e.key === "C") {
      setValue("")
      console.log("clear")
    }
    else if (!isNaN(e)) {
      setValue(e.target.value)
      console.log("number")
    }
  }

  const handleKeyDown = () => {
  }

  return (
    <div className="App">
      <div className='timer'>
        <input className="numb" type="text" placeholder='_' value={value} onKeyUp={(event) => event.target.style.width = (event.target.value.length + 1.2) + 'ch'} onKeyDown={handleValidateKey} onChange={handleKeyDown} />
        {/* <div className='outer'>
          <div className='inner'>
            <div className='element'>minutes</div>
            <div className='element'>hours</div>
          </div>
        </div> */}
        <input className="mode" type="text" list='minh' />
        <datalist id='minh'>
          <option value="minutes "></option>
          <option value="hours "></option>
        </datalist>
      </div>
      <div className='footer'> Time saved: 100 hours &nbsp; &nbsp;</div>
    </div>
  );
}

export default App;
